main_template:
---
{{ yaml_frontmatter }}
---

{% if abstract %}
## Abstract

{{ abstract }}
{% endif %}

{{ content }}

{% if references %}
## References

{{ references }}
{% endif %}

yaml_frontmatter:
title: "{{ title }}"
{% if author %}author: "{{ author }}"{% endif %}
{% if date %}date: "{{ date }}"{% endif %}
{% if course %}subtitle: "{{ course }}"{% endif %}
format:
  pdf:
    documentclass: {{ documentclass }}
    geometry: {{ geometry }}
    fontsize: {{ fontsize }}
    linestretch: {{ linestretch }}
    toc: {{ toc }}
    toc-depth: 3
    number-sections: {{ number_sections }}
    colorlinks: true
    cite-method: biblatex
    keep-tex: false
    pdf-engine: xelatex
    include-in-header:
      - text: |
          \usepackage{fancyhdr}
          \pagestyle{fancy}
          \fancyhf{}
          \fancyhead[L]{{ '{' }}{{ title }}{{ '}' }}
          \fancyhead[R]{\thepage}
          \renewcommand{\headrulewidth}{0.4pt}
